import React, { useState } from 'react';

function ToDoItem(props) {

  const [text, setText] = useState(false);

  const handleClick = () => {
    setText(!text)
  }


  return (
    <li

      onClick={handleClick}

      style={{ textDecoration: text ? "line-through" : null }}>{props.name}</li>

  );
}

export default ToDoItem;
